package arraypracticeprograms;

public class Grid_5cross5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int grid[][]=new int[5][5];
		for(int i=0;i<grid.length;i++)
		{
			for(int j=0;j<grid.length;j++)
			{
				System.out.print(" "+"-");
			}
			System.out.println();
		}
		

	}

}
