package pattern;

public class Pattern_1_23_345_4567 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int row=1;row<=5;row++)
		{
			int a=row;
			for(int col=1;col<=row;col++)
			{
				
				System.out.print(a);
				a++;
				
			}
			
			System.out.println();
			
		}

	}

}
