package simpleprograms;

public class Square_Root {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int no=16,div=2;
		while(no/2>div)
		{
			if(no/div==div)
			{
				System.out.println(div+" root");
				break;
			}
			div++;
		}
		
		

	}

}
