package string;

public class Coversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="101";
		int n=Integer.parseInt(s);
		
		int m=n+10;		
		System.out.println(m);
		int  i=100;
		String s1=String.valueOf(i);
		System.out.println(s1+100);


	}

}
